const $ = new Env('直播间红包雨1');
const fs = require('fs');
const qiniu = require('qiniu')
const notify = $.isNode() ? require('./sendNotify') : '';
let bodyList = [
  'area=12_904_908_57903&body=%7B%22liveId%22%3A%223075803%22%7D&build=167454&client=apple&clientVersion=9.3.0&d_brand=apple&d_model=iPhone10%2C2&eid=eidIF3CF0112RTIyQTVGQTEtRDVCQy00Qg%3D%3D6HAJa9%2B/4Vedgo62xKQRoAb47%2Bpyu1EQs/6971aUvk0BQAsZLyQAYeid%2BPgbJ9BQoY1RFtkLCLP5OMqU&isBackground=Y&joycious=202&lang=zh_CN&networkType=wifi&networklibtype=JDNetworkBaseAF&openudid=53f4d9c70c1c81f1c8769d2fe2fef0190a3f60d2&osVersion=14.2&partner=apple&rfs=0000&scope=01&screen=1242%2A2208&sign=084031f5f0f0db7560faecfa7bebe10f&st=1608527053488&sv=122&uts=0f31TVRjBSvsFfn0ufIgE3oCLKHFjQgCiqGvVRu1CFavM2wQIR2wAfmwZ8N/sIXSICVB10KvlrmgYsWKS%2Beyua1UfLcs8MKSUj38XtXafhjLpt3CQPbUk4cLaVWQVzSjwK8ot1cThY8chuZNpGdiEtQt7mXtGdJesfNisWM%2BrEodeK147oZJ1Q4oi8utYAGV4Q5tJJc/NoyBlrrJhoRbYQ%3D%3D&uuid=hjudwgohxzVu96krv/T6Hg%3D%3D&wifiBssid=55287765f23154691a91b6a7aa9859a0',
  
  ]

!(async () => {
  for(let i=0;i<bodyList.length;++i){
    await getRedRain(bodyList[i]);
    if($.activityId) {
      await writeFile();
      break
    }
  }
  if($.activityId) {
    await notify.sendNotify(`${new Date($.startTime).getHours()}点${$.name}更新成功！`,$.activityId);
  }else{
    await notify.sendNotify(`${new Date($.startTime).getHours()}点${$.name}无法更新，请检查！`,'');
  }
})()
  .catch((e) => {
    $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
  })
  .finally(() => {
    $.done();
  })


async function writeFile() {
  if(!$.activityId) return
  const info = {
    activityId:$.activityId,
    startTime:$.startTime,
    endTime:$.endTime
  }
  await fs.writeFileSync('jd_live_redRain.json', JSON.stringify(info));
  console.log(`文件写入成功`);
  // 覆盖文件，获取token
  const accessKey = process.env.QINIU_AK;
  const secretKey = process.env.QINIU_SK;
  const mac = new qiniu.auth.digest.Mac(accessKey, secretKey);
  const localFile = "jd_live_redRain.json";
  const putPolicy = new qiniu.rs.PutPolicy({
    scope: "nuist:" + localFile
  });
  const uploadToken = putPolicy.uploadToken(mac);

  // 上传文件
  const formUploader = new qiniu.form_up.FormUploader({
    scope: "nuist:" + localFile
  });
  const putExtra = new qiniu.form_up.PutExtra();
  const key = 'jd_live_redRain.json';
  formUploader.putFile(uploadToken, key, localFile, putExtra, function(respErr,
                                                                       respBody, respInfo) {
    if (respErr) {
      throw respErr;
    }
    if (respInfo.statusCode == 200) {
      console.log(respBody);
    } else {
      console.log(respInfo.statusCode);
      console.log(respBody);
    }
  });

  // 刷新缓存
  let urlsToRefresh = [
    'http://ql4kk90rw.hb-bkt.clouddn.com/jd_live_redRain.json'
  ];
  let cdnManager = new qiniu.cdn.CdnManager(mac);

  cdnManager.refreshUrls(urlsToRefresh, function(err, respBody, respInfo) {
    if (err) {
      throw err;
    }
    if (respInfo.statusCode === 200) {
      console.log('刷新成功！')
    }
  });

}

function getRedRain(body) {
  return new Promise(resolve => {
    $.post(taskPostUrl('liveActivityV842',body), async (err, resp, data) => {
      try {
        if (err) {
          console.log(`${JSON.stringify(err)}`)
          console.log(`${$.name} API请求失败，请检查网路重试`)
        } else {
          if (safeGet(data)) {
            data = JSON.parse(data);
            if (data.data && data.data.iconArea) {
              let act = data.data.iconArea.filter(vo=>vo['type']==="platform_red_packege_rain")[0]
              if (act) {
                let url = act.data.activityUrl
                $.activityId = url.substr(url.indexOf("id=") + 3)
                $.startTime = act.startTime
                $.endTime = act.endTime
                console.log(`下一场红包雨开始时间：${new Date(act.startTime)}`)
                console.log(`下一场红包雨结束时间：${new Date(act.endTime)}`)
              }
            } else {
              console.log(`暂无红包雨`)
            }
          }
        }
      } catch (e) {
        $.logErr(e, resp)
      } finally {
        resolve();
      }
    })
  })
}


function taskPostUrl(function_id, body = {}) {
  return {
    url: `https://api.m.jd.com/client.action?functionId=${function_id}`,
    body: body,
    headers: {
      'Host': 'api.m.jd.com',
      'content-type': 'application/x-www-form-urlencoded',
      'accept': '*/*',
      'user-agent': 'JD4iPhone/167408 (iPhone; iOS 14.2; Scale/3.00)',
      'accept-language': 'zh-Hans-JP;q=1, en-JP;q=0.9, zh-Hant-TW;q=0.8, ja-JP;q=0.7, en-US;q=0.6',
      //"Cookie": cookie,
    }
  }
}

function safeGet(data) {
  try {
    if (typeof JSON.parse(data) == "object") {
      return true;
    }
  } catch (e) {
    console.log(e);
    console.log(`京东服务器访问数据为空，请检查自身设备网络情况`);
    return false;
  }
}

